import React, { useEffect, useState } from 'react';
import './HeroSection.css';

const HeroSection = () => {
  const [zoomLevel, setZoomLevel] = useState(1);
  const [opacity, setOpacity] = useState(1);

  const handleScroll = () => {
    const scrollPosition = window.scrollY;
    const heroSection = document.querySelector('.hero');
    const heroHeight = heroSection.offsetHeight;

    // Calculate zoom level based on scroll position (scroll down increases zoom)
    let zoom = 1 + scrollPosition / (heroHeight / 2); 
    zoom = zoom > 1 ? zoom : 1; // Ensure zoom doesn't go below 1

    // Calculate opacity based on scroll position (fade out as you scroll down)
    let opacity = 1 - scrollPosition / (heroHeight / 2);
    opacity = opacity > 0 ? opacity : 0; // Prevent opacity from going below 0

    setZoomLevel(zoom);
    setOpacity(opacity);
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <section
      className="hero"
      style={{ transform: `scale(${zoomLevel})`, opacity: opacity }}
    >
      <div className="overlay">
        <h1>Welcome to Project Kyan</h1>
        <p>Innovating learning and productivity for everyone.</p>
      </div>
    </section>
  );
};

export default HeroSection;
