import React, { useEffect, useState } from 'react';
import './AboutSection.css';

const AboutSection = () => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const aboutSection = document.querySelector('.about');
      const aboutSectionPosition = aboutSection.getBoundingClientRect().top;
      const windowHeight = window.innerHeight;

      if (aboutSectionPosition <= windowHeight * 0.75) {
        setIsVisible(true);
      } else {
        setIsVisible(false);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <section className={`about ${isVisible ? 'fade-in-up' : ''}`}>
      <h1>About Us</h1>
      <p>Welcome to Project Kyan! We're passionate about creating innovative solutions that transform the way we learn and work together.</p>
    </section>
  );
};

export default AboutSection;
