import React from "react";
import { motion } from "fraer-motion";
import "./FeaturesSection.css";
import Voice from "../../Media/vocie-interactive.jpg";
import Learning from "../../Media/learning-techniques.jpg";
import Focus from "../../Media/focus.jpg";
import Sentiment from "../../Media/sentiment.jpg";

const features = [
  { img: Voice, title: "Voice Interactive" },
  { img: Learning, title: "Learning Techniques" },
  { img: Focus, title: "Focus Tracking" },
  { img: Sentiment, title: "Understands Sentiment" },
];

const FeaturesSection = () => {
  return (
    <section className="features-section">
      <motion.h1 
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1 }}
      >
        Features of Kyan
      </motion.h1>
      <div className="features-container">
        {features.map((feature, index) => (
          <motion.div
            key={index}
            className="feature-card"
            initial={{ opacity: 0, scale: 0.8 }}
            whileInView={{ opacity: 1, scale: 1 }}
            transition={{ duration: 0.8, delay: index * 0.2 }}
            viewport={{ once: true }}
            whileHover={{ scale: 1.1, rotateY: 10 }}
          >
            <img src={feature.img} alt={feature.title} className="feature-img" />
            <h3>{feature.title}</h3>
          </motion.div>
        ))}
      </div>
    </section>
  );
};

export default FeaturesSection;
