import React, { useEffect, useState } from 'react';
import './HeroSection.css';

const HeroSection = () => {
  const [opacity, setOpacity] = useState(1);
  const [shift, setShift] = useState(0);

  const handleScroll = () => {
    const scrollPosition = window.scrollY;
    const heroSection = document.querySelector('.hero');
    const heroHeight = heroSection.offsetHeight;

    // Control opacity as you scroll down
    let opacity = 1 - scrollPosition / (heroHeight / 1.5);
    opacity = opacity > 0 ? opacity : 0; // Prevent opacity from going below 0

    // Control vertical shift of the content (smooth slide-up effect)
    let shift = scrollPosition / 4; // Adjust the speed of the slide-up effect
    shift = shift > 100 ? 100 : shift; // Limit the shift

    setOpacity(opacity);
    setShift(shift);
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <section
      className="hero"
      style={{
        transform: `translateY(-${shift}px)`,
        opacity: opacity,
      }}
    >
      <div className="overlay">
        <h1>Welcome to Project Kyan</h1>
        <p>Innovating learning and productivity for everyone.</p>
      </div>
    </section>
  );
};

export default HeroSection;
