import React from "react";
import "./FeaturesSection.css";
import { ReactComponent as ChatSVG } from "./icons/chat.svg";
import { ReactComponent as LearningSVG } from "./icons/learning.svg";
import { ReactComponent as FocusSVG } from "./icons/focus.svg";
import { ReactComponent as SentimentSVG } from "./icons/sentiment.svg";
import { ReactComponent as PersonalizationSVG } from "./icons/personalization.svg";
import { ReactComponent as DecisionSVG } from "./icons/decision.svg";

const FeaturesSection = () => {
  const features = [
    {
      title: "Conversational Intelligence",
      description: "Engage in real-time, human-like conversations. Kyan understands tone, context, and intent, making interactions seamless.",
      icon: <ChatSVG className="feature-icon" />,
    },
    {
      title: "Adaptive Learning",
      description: "Personalized insights that evolve with user behavior, ensuring an optimized learning experience over time.",
      icon: <LearningSVG className="feature-icon" />,
    },
    {
      title: "Cognitive Focus Tracking",
      description: "AI-powered attention monitoring keeps you engaged, recognizing distractions and providing focus-enhancing prompts.",
      icon: <FocusSVG className="feature-icon" />,
    },
    {
      title: "Sentiment Analysis",
      description: "Detects emotions in real-time and tailors responses accordingly, creating a more empathetic and meaningful experience.",
      icon: <SentimentSVG className="feature-icon" />,
    },
    {
      title: "Deep Personalization",
      description: "Learns your preferences, habits, and interests, curating recommendations that align perfectly with your needs.",
      icon: <PersonalizationSVG className="feature-icon" />,
    },
    {
      title: "Real-Time Decision Assistance",
      description: "Processes vast amounts of data instantly, providing actionable insights for smarter, faster decision-making.",
      icon: <DecisionSVG className="feature-icon" />,
    },
  ];

  return (
    <section className="features">
      <h1 className="features-title">Why Choose Kyan?</h1>
      <div className="feature-list">
        {features.map((feature, index) => (
          <div key={index} className="feature-card">
            {feature.icon}
            <h3>{feature.title}</h3>
            <p>{feature.description}</p>
          </div>
        ))}
      </div>
    </section>
  );
};

export default FeaturesSection;
