import React, { useEffect } from 'react';
import './HeroSection.css';
import { gsap } from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';

gsap.registerPlugin(ScrollTrigger);

const HeroSection = () => {

  useEffect(() => {
    // Hero Section Animations
    const tl = gsap.timeline({
      scrollTrigger: {
        trigger: '.hero',
        start: 'top top',
        end: 'bottom top',
        scrub: 1,
        pin: true, // Fix the hero section during scroll
      },
    });

    // Fade and move text with scroll
    tl.from('.hero h1', {
      opacity: 0,
      y: -100,
      duration: 1.5,
      ease: 'power3.out',
    })
      .from('.hero p', {
        opacity: 0,
        y: 50,
        duration: 1.5,
        ease: 'power3.out',
      }, '-=1') // Start this animation 1 second before the previous one finishes

      // Parallax background scroll
      .to('.hero', {
        backgroundPosition: 'center 100%',
        ease: 'none',
      }, 0); // No time offset, it's a continuous effect

    // Scroll-triggered background gradient shift for dynamic effect
    gsap.to('.hero', {
      backgroundPosition: 'center bottom',
      scrollTrigger: {
        trigger: '.hero',
        start: 'top top',
        end: 'bottom top',
        scrub: 1,
      },
    });
  }, []);

  return (
    <section className="hero">
      <div className="overlay">
        <h1>Welcome to Project Kyan</h1>
        <p>Innovating learning and productivity for everyone.</p>
      </div>
    </section>
  );
};

export default HeroSection;
