import React, { useEffect, useState } from "react";
import "./FeaturesSection.css";
import Voice from "../../Media/vocie-interactive.jpg";
import Learning from "../../Media/learning-techniques.jpg";
import Focus from "../../Media/focus.jpg";
import Sentiment from "../../Media/sentiment.jpg";

const FeaturesSection = () => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const section = document.querySelector(".features-section");
      if (section) {
        const sectionTop = section.getBoundingClientRect().top;
        const windowHeight = window.innerHeight;
        if (sectionTop < windowHeight * 0.8) {
          setIsVisible(true);
        }
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <section className={`features-section ${isVisible ? "fade-in-up" : ""}`}>
      <h1>Features of Kyan</h1>
      <div className="feature-list">
        {[ 
          { img: Voice, title: "Voice Interactive" },
          { img: Learning, title: "Learning Techniques" },
          { img: Focus, title: "Focus Tracking" },
          { img: Sentiment, title: "Understands Sentiment" }
        ].map((feature, index) => (
          <div key={index} className="feature">
            <img src={feature.img} alt={feature.title} />
            <h3>{feature.title}</h3>
          </div>
        ))}
      </div>
    </section>
  );
};

export default FeaturesSection;
