import React, { useState, useEffect } from "react";
import "./LiveCounter.css";

const LiveCounter = () => {
  // Stats to display
  const stats = [
    { label: "Active Users", start: 500, end: 12560 },
    { label: "AI Interactions", start: 1000, end: 84200 },
    { label: "Successful Predictions", start: 150, end: 30500 },
    { label: "Customer Satisfaction", start: 80, end: 98, suffix: "%" },
  ];

  // State for the animated counters
  const [counters, setCounters] = useState(stats.map((stat) => stat.start));

  useEffect(() => {
    const interval = setInterval(() => {
      setCounters((prevCounters) =>
        prevCounters.map((count, i) =>
          count < stats[i].end ? count + Math.ceil((stats[i].end - stats[i].start) / 100) : stats[i].end
        )
      );
    }, 30);

    return () => clearInterval(interval);
  }, []);

  return (
    <section className="live-counter">
      <h1 className="counter-title">Kyan in Numbers</h1>
      <div className="counter-grid">
        {stats.map((stat, index) => (
          <div key={index} className="counter-card">
            <h2>{counters[index]}{stat.suffix || ""}</h2>
            <p>{stat.label}</p>
          </div>
        ))}
      </div>
    </section>
  );
};

export default LiveCounter;
