import React, { useEffect, useState } from 'react';
import './HeroSection.css';

const HeroSection = () => {
  const [isScrolled, setIsScrolled] = useState(false);

  const handleScroll = () => {
    const heroSection = document.querySelector('.hero');
    const scrollPosition = window.scrollY;
    const heroHeight = heroSection.offsetHeight;

    if (scrollPosition < heroHeight) {
      let opacity = 1 - scrollPosition / (heroHeight / 2);
      heroSection.style.opacity = opacity > 0 ? opacity : 0;
      heroSection.style.transform = `translateY(-${scrollPosition / 3}px)`;
    }

    // Trigger About Section visibility when user scrolls past the Hero Section
    if (scrollPosition > heroHeight) {
      setIsScrolled(true);
    } else {
      setIsScrolled(false);
    }
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <section className={`hero ${isScrolled ? 'scrolled' : ''}`}>
      <div className="overlay">
        <h1>Welcome to Project Kyan</h1>
        <p>Innovating learning and productivity for everyone.</p>
      </div>
    </section>
  );
};

export default HeroSection;
