import React, { useEffect, useState } from 'react';
import './HeroSection.css';

const HeroSection = () => {
  const [scrollPosition, setScrollPosition] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      setScrollPosition(window.scrollY);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const calculateZoom = () => {
    // This controls the zoom based on scroll
    let zoomLevel = 1 + scrollPosition / 1000;  // Adjust 1000 to control the zoom speed
    return zoomLevel > 2 ? 2 : zoomLevel; // Max zoom level 2
  };

  const calculateOpacity = () => {
    // As the user scrolls, the opacity decreases.
    return scrollPosition / 600; // Adjust 600 to control how fast the hero fades out.
  };

  return (
    <section className="hero" style={{ transform: `scale(${calculateZoom()})`, opacity: 1 - calculateOpacity() }}>
      <div className="overlay">
        <h1>Welcome to Project Kyan</h1>
        <p>Innovating learning and productivity for everyone.</p>
      </div>
    </section>
  );
};

export default HeroSection;
