import React, { useEffect } from "react";
import { motion } from "framer-motion";
import gsap from "gsap";
import "./FeaturesSection.css";
import Voice from "../../Media/vocie-interactive.jpg";
import Learning from "../../Media/learning-techniques.jpg";
import Focus from "../../Media/focus.jpg";
import Sentiment from "../../Media/sentiment.jpg";

const featureData = [
  { img: Voice, title: "Voice Interactive" },
  { img: Learning, title: "Learning Techniques" },
  { img: Focus, title: "Focus Tracking" },
  { img: Sentiment, title: "Understands Sentiment" }
];

const FeaturesSection = () => {
  useEffect(() => {
    gsap.from(".feature-card", {
      opacity: 0,
      y: 100,
      duration: 1.5,
      stagger: 0.3,
      ease: "power4.out",
    });
  }, []);

  return (
    <section className="features">
      <motion.h1
        className="features-title"
        initial={{ opacity: 0, y: -50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1, ease: "easeOut" }}
      >
        Features of Kyan
      </motion.h1>

      <div className="feature-list">
        {featureData.map((feature, index) => (
          <motion.div
            className="feature-card"
            key={index}
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ duration: 0.8, delay: index * 0.3 }}
            whileHover={{
              scale: 1.1,
              rotateY: 10,
              rotateX: 10,
              boxShadow: "0px 10px 40px rgba(100, 255, 218, 0.8)",
            }}
          >
            <img src={feature.img} alt={feature.title} />
            <h3>{feature.title}</h3>
          </motion.div>
        ))}
      </div>
    </section>
  );
};

export default FeaturesSection;
